package main

import (
	"golang.org/x/tour/reader"
)

// MyReader is
type MyReader struct{}

// Add a Read([]byte) (int, error) method to MyReader.
func (r MyReader) Read(b []byte) (int, error) {

	for i := range b {
		b[i] = 65
	}
	return 1, nil

}

func main() {
	reader.Validate(MyReader{})
}

